function initializeNumberField(){var e=Array.from(document.getElementsByClassName("srfm-number-block"));if(e)for(const t of e){const r=t.querySelector("input");r&&r.addEventListener("input",e=>{var t=r.getAttribute("format-type");let n=e.target.value;"none"!==t&&("non-decimal"===t?n=n?.replace(/[^0-9]/g,""):1<(n=n?.replace(/[^0-9.]/g,""))?.split(".").length-1&&(n=n?.replace(/\.+$/g,"")),r.value=n)})}}document.addEventListener("DOMContentLoaded",initializeNumberField);