function initializeURL(){var e=document.getElementsByClassName("srfm-url-block");if(e)for(const t of e){const r=t.querySelector(".srfm-input-url"),s=t.querySelector(".srfm-error-message");r&&r.addEventListener("change",()=>{new RegExp("^(https?:\\/\\/)?((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|((\\d{1,3}\\.){3}\\d{1,3}))(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*(\\?[;&a-z\\d%_.~+=-]*)?(\\#[-a-z\\d_]*)?$","i").test(r.value)?(s.style.display="none",t.classList.remove("srfm-error")):(t.classList.add("srfm-error"),s.style.display="block",s.innerText="Please enter a valid URL.")})}}document.addEventListener("DOMContentLoaded",initializeURL);