function initializeCheckbox(){var e=document.querySelectorAll(".srfm-checkbox-container");if(e)for(let r=0;r<e.length;r++){var c=e[r].closest("form");const o=getComputedStyle(c).getPropertyValue("--srfm-primary-color");e[r].querySelectorAll(".srfm-classic-checkbox-input").forEach(r=>{""===o?r.classList.add("!srfm-text-[#0084C7]","focus:!srfm-ring-[#0084C7]","checked:!srfm-bg-[#0084C7]","checked:!srfm-border-none"):r.classList.add("!srfm-text-srfm_primary_color","focus:!srfm-ring-srfm_primary_color","checked:!srfm-bg-srfm_primary_color","checked:!srfm-border-none")})}}document.addEventListener("DOMContentLoaded",initializeCheckbox);