function initializeRatings(){var e=document.querySelectorAll(".srfm-rating-block");e&&e.forEach(e=>{e=e.querySelectorAll(".srfm-icon");e&&e.forEach(e=>{e.addEventListener("click",function(e){handleSingleRating(e,"click")}),e.addEventListener("mouseover",function(e){handleSingleRating(e,"hover")}),e.addEventListener("mouseout",function(e){handleSingleRating(e,"mouse-out")})})})}function handleSingleRating(e,t){var i=e.currentTarget.closest("ul").children,n=parseInt(e.currentTarget.getAttribute("data-value")),l=e.currentTarget.closest(".srfm-block").querySelector(".srfm-input-rating");if(n){for(let e=0;e<i.length;e++)i[e].classList.remove("srfm-fill");for(let e=0;e<n;e++)i[e].classList.add("srfm-fill");if("click"===t&&l&&l.setAttribute("value",n),"mouse-out"===t&&l&&i){for(let e=0;e<i.length;e++)i[e].classList.remove("srfm-fill");if(l.getAttribute("value"))for(let e=0;e<l.getAttribute("value");e++)i[e].classList.add("srfm-fill")}}}document.addEventListener("DOMContentLoaded",initializeRatings);