function initializeNumberField(){var e=Array.from(document.getElementsByClassName("srfm-input-number-container"));if(e)for(const t of e){const i=t.querySelector("input");i&&i.addEventListener("input",e=>{var t=i.getAttribute("format-type");let n=e.target.value;"none"!==t&&("non-decimal"===t?n=n.replace(/[^0-9]/g,""):1<(n=n.replace(/[^0-9.]/g,"")).split(".").length-1&&(n=n.replace(/\.+$/g,"")),i.value=n)})}}document.addEventListener("DOMContentLoaded",initializeNumberField);